import { Plus_Jakarta_Sans } from "next/font/google";
import "./globals.css";
import Header from "@/Component/Header";
import Footer from "@/Component/Footer";
import DynamicShaderBackground from "@/Component/DynamicShaderBackground";
import { Toaster } from "react-hot-toast";

const plusJakarta = Plus_Jakarta_Sans({
  variable: "--font-plus-jakarta",
  subsets: ["latin"],
  display: "swap",
});

export const metadata = {
  title: "MZ KODERS - Tech & Digital Solutions",
  description: "Modern web development, app development, AI, and digital services.",
};

export default function RootLayout({ children }) {
  return (
    <html lang="en" className={plusJakarta.variable}>
      <body className="font-sans antialiased text-white relative min-h-screen bg-black">
        <DynamicShaderBackground />
        <Toaster
          position="top-right"
          toastOptions={{
            duration: 5000,
            style: {
              background: "rgba(30, 58, 95, 0.95)",
              color: "#fff",
              border: "1px solid rgba(255,255,255,0.2)",
            },
          }}
        />
        <div className="relative z-10 flex min-h-screen flex-col">
          <Header />
          <main className="flex-1">{children}</main>
          <Footer />
        </div>
      </body>
    </html>
  );
}
